mapper.update:
UPDATE STUDYROOM 
SET STATE=(SELECT GENDER FROM USER_INFO WHERE ID=#{id}), START_TIME=CURTIME(), END_TIME=ADDTIME(CURTIME(), '4:00'), ID=#{id}
WHERE NUM=#{num}

mapper.select:
SELECT NUM
FROM STUDYROOM
WHERE ID=#{id}

mapper.update:
UPDATE STUDYROOM
SET STATE=0, START_TIME=NULL, END_TIME=NULL, ID=NULL
WHERE NUM=#{num}
