mapper.select:
SELECT BNAME, BWRITER, BCOMPANY, BYEAR, HOPE_DATE, RESULT
FROM HOPE_BOOK
WHERE ID=#{id}

SELECT A.NUM, MSELECT, TITLE, MIN_DATE, (SELECT COUNT(NUM) FROM MINWON_REP WHERE A.NUM=NUM) as re_cnt
FROM MINWON A
WHERE ID=#{id}

SELECT COUNT(BNO) 
FROM RESERVE_BOOK 
WHERE BNO=#{bno}

SELECT COUNT(ID) 
FROM LATE 
WHERE ID=#{id}
